mapper.select:
select b.recommend_category, b.title, u.username, b.create_date, b.view_cnt, b.like_cnt, b.board_no, u.role, u.user_no, b.recommend_name, b.recommend_src, b.recommend_img from board b inner join user u on b.user_no = u.user_no and b.recommend_category = '영화' where u.role = 'ROLE_USER'
and u.username like concat('%', #{word}, '%')
order by field(role, 'ROLE_ADMIN', 'ROLE_USER'), create_date desc limit #{sno}, #{eno}

select b.recommend_category, b.title, u.username, b.create_date, b.view_cnt, b.like_cnt, b.board_no, u.role, u.user_no, b.recommend_name, b.recommend_src, b.recommend_img from board b inner join user u on b.user_no = u.user_no and b.recommend_category = '도서' where u.role = 'ROLE_USER'
and u.username like concat('%', #{word}, '%')
order by field(role, 'ROLE_ADMIN', 'ROLE_USER'), create_date desc limit #{sno}, #{eno}

select b.recommend_category, b.title, u.username, b.create_date, b.view_cnt, b.like_cnt, b.board_no, u.role, u.user_no from board b inner join user u on b.user_no = u.user_no and b.recommend_category = '음악' where u.role = 'ROLE_USER'
and u.username like concat('%', #{word}, '%')
order by field(role, 'ROLE_ADMIN', 'ROLE_USER'), create_date desc limit #{sno}, #{eno}
