mapper.select:
select b.weather_category, b.title, u.username, b.create_date, b.view_cnt, b.like_cnt, b.board_no, u.role from board b inner join user u on b.user_no = u.user_no and b.weather_category is not null
u.username like concat('%', #{word}, '%')
order by field(role, 'ROLE_ADMIN', 'ROLE_USER'), create_date desc limit #{sno}, #{eno}

select b.weather_category, b.title, u.username, b.create_date, b.view_cnt, b.like_cnt, b.board_no, u.role from board b inner join user u on b.user_no = u.user_no and b.weather_category = '맑음'
u.username like concat('%', #{word}, '%')
order by field(role, 'ROLE_ADMIN', 'ROLE_USER'), create_date desc limit #{sno}, #{eno}

select b.weather_category, b.title, u.username, b.create_date, b.view_cnt, b.like_cnt, b.board_no, u.role from board b inner join user u on b.user_no = u.user_no and b.weather_category = '흐림'
u.username like concat('%', #{word}, '%')
order by field(role, 'ROLE_ADMIN', 'ROLE_USER'), create_date desc limit #{sno}, #{eno}

select b.weather_category, b.title, u.username, b.create_date, b.view_cnt, b.like_cnt, b.board_no, u.role from board b inner join user u on b.user_no = u.user_no and b.weather_category = '비'
u.username like concat('%', #{word}, '%')
